    // futex word 用于 condvar 的等待和唤醒
    int cond_futex;
    pthread_mutex_t *mutex;
    unsigned int total_seq;    // 总的 signal/broadcast 次数
    unsigned int wakeup_seq;   // 已被唤醒处理的次数
    // 其他字段，如等待者数量、时钟ID等...
};
</code></pre>

