C++<pre><code>#include &lt;algorithm&gt;
#include &lt;filesystem&gt;
#include &lt;fstream&gt;
#include &lt;iostream&gt;

int main()
{
    const std::filesystem::path sandbox{"sandbox"};
    std::filesystem::create_directories(sandbox/"dir1"/"dir2");
    std::ofstream{sandbox/"file1.txt"};
    std::ofstream{sandbox/"file2.txt"};

    std::cout &lt;&lt; "directory_iterator:\n";
    // directory_iterator can be iterated using a range-for loop
    for (auto const&amp; dir_entry : std::filesystem::directory_iterator{sandbox}) 
        std::cout &lt;&lt; dir_entry.path() &lt;&lt; '\n';

    std::cout &lt;&lt; "\ndirectory_iterator as a range:\n";
    // directory_iterator behaves as a range in other ways, too
    std::ranges::for_each(
        std::filesystem::directory_iterator{sandbox},
        [](const auto&amp; dir_entry) { std::cout &lt;&lt; dir_entry &lt;&lt; '\n'; });

    std::cout &lt;&lt; "\nrecursive_directory_iterator:\n";
    for (auto const&amp; dir_entry : std::filesystem::recursive_directory_iterator{sandbox}) 
        std::cout &lt;&lt; dir_entry &lt;&lt; '\n';

    // delete the sandbox dir and all contents within it, including subdirs
    std::filesystem::remove_all(sandbox);
}
// Possible output:
// directory_iterator:
// "sandbox/file2.txt"
// "sandbox/file1.txt"
// "sandbox/dir1"

// directory_iterator as a range:
// "sandbox/file2.txt"
// "sandbox/file1.txt"
// "sandbox/dir1"

// recursive_directory_iterator:
// "sandbox/file2.txt"
// "sandbox/file1.txt"
// "sandbox/dir1"
// "sandbox/dir1/dir2"
</code></pre>

C++<pre><code>// parallel accumulate
template&lt;typename Iterator,typename T&gt;
struct accumulate_block  {
  void operator()(Iterator first,Iterator last,T&amp; result)  {  result=std::accumulate(first,last,result);
  }
};
template&lt;typename Iterator,typename T&gt;
T parallel_accumulate(Iterator first,Iterator last,T init)
{
  unsigned long const length=std::distance(first,last);
  if(!length) // 1
      return init;
  unsigned long const min_per_thread=25;
  unsigned long const max_threads=  (length+min_per_thread-1)/min_per_thread; // 2
  unsigned long const hardware_threads=  std::thread::hardware_concurrency();  unsigned long const num_threads= // 3
  std::min(hardware_threads != 0 ? hardware_threads : 2, max_threads);  unsigned long const block_size=length/num_threads; // 4
  std::vector&lt;T&gt; results(num_threads);
  std::vector&lt;std::thread&gt; threads(num_threads-1); // 5
  Iterator block_start=first;
  for(unsigned long i=0; i &lt; (num_threads-1); ++i)
  {
      Iterator block_end=block_start;
      std::advance(block_end,block_size); // 6
      threads[i]=std::thread( // 7
          accumulate_block&lt;Iterator,T&gt;(),
          block_start,block_end,std::ref(results[i]));
      block_start=block_end; // 8
  }
  accumulate_block&lt;Iterator,T&gt;()(  block_start,last,results[num_threads-1]); // 9
  for (auto&amp; entry : threads)  entry.join(); // 10
  return std::accumulate(results.begin(),results.end(),init); // 11
}
</code></pre>

