            run_next_app();
        }
        Trap::Exception(Exception::IllegalInstruction) =&gt; {
            println!("[kernel] IllegalInstruction in application, kernel killed it.");
            run_next_app();
        }
        _ =&gt; {
            panic!("Unsupported trap {:?}, stval = {:#x}!", scause.cause(), stval);
        }
    }
    cx
}
